importScripts('https://cdn.by.wonderpush.com/sdk/1.1/wonderpush-loader.min.js');
WonderPush.init({
  webKey: (location.search.match(/[?&]webKey=([^&]*)/) || [])[1],
});

// Receive notification.
self.addEventListener('push', function () {
  window.navigator.setAppBadge();
});

// Clicked notification.
self.addEventListener('notificationclick', function () {
  window.navigator.clearAppBadge();
});